                type="range"
                id="input"
                className="in slider-progress"
                onChange={changePay}
                min={1000000}
                max={6000000}
                step={100000}
                value={pay}
            />

            <div>
                <input
                    type="number"
                    id="outInput"
                    className="out"
                    value={pay}
                    onChange={changePay}
                    placeholder='                                                ₽'
                  
                /></div>
  </div>
  <div class="leaseTerm">

