import { useState } from "react";

function Input() {
  const [pay, setPay] = useState("");
  const changePay = (event) => {
    setPay(event.target.value);
    const rub = () => {};
  };
  for (let e of document.querySelectorAll(
    'input[type="range"].slider-progress'
  )) {
    e.style.setProperty("--value", e.value);
    e.style.setProperty("--min", e.min === "" ? "0" : e.min);
    e.style.setProperty("--max", e.max === "" ? "100" : e.max);
    e.addEventListener("input", () => e.style.setProperty("--value", e.value));
  }
  return (
    <div class="container">
      <h1 className="top">
        Рассчитайте стоимость
        <br /> автомобиля в лизинг
      </h1>
      <div class="top"></div>
      <div class="carPrice">
        <h2 className="car">Стоимость автомобиля</h2>
        <input
          type="range"
          id="input"
          className="in slider-progress"
          onChange={changePay}
          min={1000000}
          max={6000000}
          step={100000}
          value={pay}
        />

        <div>
          <input
            type="number"
            id="outInput"
            className="out"
            value={pay}
            onChange={changePay}
            placeholder="                                                ₽"
          />
        </div>
      </div>
      <div class="cumContract">
      <h2 className="car">Первоначальный взнос</h2>
        <input
          type="range"
          id="input"
          className="in slider-progress"
          onChange={changePay}
          min={1000000}
          max={6000000}
          step={100000}
          value={pay}
        />

        <div>
          <input
            type="number"
            id="outInput"
            className="out"
            value={pay}
            onChange={changePay}
            placeholder="                                                ₽"
          />
        </div>
      </div>
      <div class="initialCost">
      <h2 className="car">Срок лизинга</h2>
        <input
          type="range"
          id="input"
          className="in slider-progress"
          onChange={changePay}
          min={1000000}
          max={6000000}
          step={100000}
          value={pay}
        />

        <div>
          <input
            type="number"
            id="outInput"
            className="out"
            value={pay}
            onChange={changePay}
            placeholder="                                                ₽"
          />
        </div>
      </div>
      <div class="leaseTerm">
      <h2 className="car">Сумма договора лизинга</h2>
      </div>
      <div class="monthPay">
      <h2 className="car">Срок лизинга</h2>
      </div>
      <div class="button">
        <button></button>
      </div>
    </div>

    // {/* /* <div className='main'>
    // //     <div className='subMain'>
    // //         <h1 className="top">Рассчитайте стоимость<br/> автомобиля в лизинг</h1>
    // //     </div>
    // //     <div className="container">
    // //         <h2 className='car'>Стоимость автомобиля</h2>
    // //         <div className='dop'>
    // //             <input
    // //                 type="range"
    // //                 id="input"
    // //                 className="in slider-progress"
    // //                 onChange={changePay}
    // //                 min={1000000}
    // //                 max={6000000}
    // //                 step={100000}
    // //                 value={pay}
    // //             />

    // //             <div>
    // //                 <input
    // //                     type="number"
    // //                     id="outInput"
    // //                     className="out"
    // //                     value={pay}
    // //                     onChange={changePay}
    // //                     placeholder='                                                ₽'
    // //                     // placeholder='1000000                                  ₽'
    // //                 />
    // //             </div>
    // //         </div>
    // //     </div>
    // // </div>/* */}
  );
}
export default Input;
